-- -----------------------------------------------------
-- procedure filtrar_blusas
-- -----------------------------------------------------

USE `mydb`;
DROP procedure IF EXISTS `mydb`.`filtrar_blusas`;

DELIMITER $$
USE `mydb`$$
CREATE DEFINER=`admin`@`%` PROCEDURE `filtrar_blusas`(
    IN _nombre_like      VARCHAR(100),
    IN _color_like       VARCHAR(30),
    IN _material_like    VARCHAR(50),
    IN _tipo_blusa_like  VARCHAR(30),
    IN _tipo_manga_like  VARCHAR(30),
    IN _precio_min       DECIMAL(10,2),
    IN _precio_max       DECIMAL(10,2),
    IN _solo_activos     TINYINT           -- NULL no filtra; 1 activos; 0 inactivos
)
BEGIN
  /* Si un parámetro llega NULL o vacío, no filtra por ese campo. */
  SELECT
      p.*,
      b.tipo_blusa,
      b.tipo_manga
  FROM Prenda p
  JOIN Blusa b ON b.idPrenda = p.idPrenda
  WHERE
      (CASE 
         WHEN _nombre_like IS NULL OR TRIM(_nombre_like) = '' THEN 1
         ELSE (LOWER(p.nombre) LIKE CONCAT('%', LOWER(TRIM(_nombre_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _color_like IS NULL OR TRIM(_color_like) = '' THEN 1
         ELSE (LOWER(p.color) LIKE CONCAT('%', LOWER(TRIM(_color_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _material_like IS NULL OR TRIM(_material_like) = '' THEN 1
         ELSE (LOWER(p.material) LIKE CONCAT('%', LOWER(TRIM(_material_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _tipo_blusa_like IS NULL OR TRIM(_tipo_blusa_like) = '' THEN 1
         ELSE (LOWER(b.tipo_blusa) LIKE CONCAT('%', LOWER(TRIM(_tipo_blusa_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _tipo_manga_like IS NULL OR TRIM(_tipo_manga_like) = '' THEN 1
         ELSE (LOWER(b.tipo_manga) LIKE CONCAT('%', LOWER(TRIM(_tipo_manga_like)), '%'))
       END)
    AND
      (CASE WHEN _precio_min IS NULL THEN 1 ELSE (p.precioUnidad >= _precio_min) END)
    AND
      (CASE WHEN _precio_max IS NULL THEN 1 ELSE (p.precioUnidad <= _precio_max) END)
    AND
      (CASE WHEN _solo_activos IS NULL THEN 1 ELSE (p.activo = _solo_activos) END);
END$$

DELIMITER ;

-- -----------------------------------------------------
-- procedure filtrar_casacas
-- -----------------------------------------------------

USE `mydb`;
DROP procedure IF EXISTS `mydb`.`filtrar_casacas`;

DELIMITER $$
USE `mydb`$$
CREATE DEFINER=`admin`@`%` PROCEDURE `filtrar_casacas`(
    IN _nombre_like      VARCHAR(100),
    IN _color_like       VARCHAR(30),
    IN _material_like    VARCHAR(50),
    IN _tipo_casaca_like VARCHAR(30),
    IN _con_capucha      TINYINT,        -- NULL no filtra; 1 sí; 0 no
    IN _precio_min       DECIMAL(10,2),
    IN _precio_max       DECIMAL(10,2),
    IN _solo_activos     TINYINT         -- NULL no filtra; 1 activos; 0 inactivos
)
BEGIN
  SELECT
      p.*,
      c.tipo_casaca,
      c.con_capucha
  FROM Prenda p
  JOIN Casaca c ON c.idPrenda = p.idPrenda
  WHERE
      (CASE WHEN _nombre_like IS NULL OR TRIM(_nombre_like) = '' 
            THEN 1 ELSE (LOWER(p.nombre)  LIKE CONCAT('%', LOWER(TRIM(_nombre_like)),  '%')) END)
    AND
      (CASE WHEN _color_like IS NULL OR TRIM(_color_like) = '' 
            THEN 1 ELSE (LOWER(p.color)   LIKE CONCAT('%', LOWER(TRIM(_color_like)),   '%')) END)
    AND
      (CASE WHEN _material_like IS NULL OR TRIM(_material_like) = '' 
            THEN 1 ELSE (LOWER(p.material)LIKE CONCAT('%', LOWER(TRIM(_material_like)),'%')) END)
    AND
      (CASE WHEN _tipo_casaca_like IS NULL OR TRIM(_tipo_casaca_like) = '' 
            THEN 1 ELSE (LOWER(c.tipo_casaca) LIKE CONCAT('%', LOWER(TRIM(_tipo_casaca_like)), '%')) END)
    AND
      (CASE WHEN _con_capucha IS NULL THEN 1 ELSE (c.con_capucha = _con_capucha) END)
    AND
      (CASE WHEN _precio_min IS NULL THEN 1 ELSE (p.precioUnidad >= _precio_min) END)
    AND
      (CASE WHEN _precio_max IS NULL THEN 1 ELSE (p.precioUnidad <= _precio_max) END)
    AND
      (CASE WHEN _solo_activos IS NULL THEN 1 ELSE (p.activo = _solo_activos) END);
END$$

DELIMITER ;

-- -----------------------------------------------------
-- procedure filtrar_faldas
-- -----------------------------------------------------

USE `mydb`;
DROP procedure IF EXISTS `mydb`.`filtrar_faldas`;

DELIMITER $$
USE `mydb`$$
CREATE DEFINER=`admin`@`%` PROCEDURE `filtrar_faldas`(
    IN _nombre_like      VARCHAR(100),
    IN _color_like       VARCHAR(30),
    IN _material_like    VARCHAR(50),
    IN _tipo_falda_like  VARCHAR(30),
    IN _con_forro        TINYINT,         -- NULL no filtra; 1 sí; 0 no
    IN _con_volantes     TINYINT,         -- NULL no filtra; 1 sí; 0 no
    IN _precio_min       DECIMAL(10,2),
    IN _precio_max       DECIMAL(10,2),
    IN _largo_min        DECIMAL(10,2),
    IN _largo_max        DECIMAL(10,2),
    IN _solo_activos     TINYINT          -- NULL no filtra; 1 activos; 0 inactivos
)
BEGIN
  SELECT
      p.*,
      f.tipo_falda,
      f.largo,
      f.con_forro,
      f.con_volantes
  FROM Prenda p
  JOIN Falda f ON f.idPrenda = p.idPrenda
  WHERE
      (CASE 
         WHEN _nombre_like IS NULL OR TRIM(_nombre_like) = '' THEN 1
         ELSE (LOWER(p.nombre) LIKE CONCAT('%', LOWER(TRIM(_nombre_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _color_like IS NULL OR TRIM(_color_like) = '' THEN 1
         ELSE (LOWER(p.color) LIKE CONCAT('%', LOWER(TRIM(_color_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _material_like IS NULL OR TRIM(_material_like) = '' THEN 1
         ELSE (LOWER(p.material) LIKE CONCAT('%', LOWER(TRIM(_material_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _tipo_falda_like IS NULL OR TRIM(_tipo_falda_like) = '' THEN 1
         ELSE (LOWER(f.tipo_falda) LIKE CONCAT('%', LOWER(TRIM(_tipo_falda_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _con_forro IS NULL THEN 1
         ELSE (f.con_forro = _con_forro)
       END)
    AND
      (CASE 
         WHEN _con_volantes IS NULL THEN 1
         ELSE (f.con_volantes = _con_volantes)
       END)
    AND
      (CASE WHEN _precio_min IS NULL THEN 1 ELSE (p.precioUnidad >= _precio_min) END)
    AND
      (CASE WHEN _precio_max IS NULL THEN 1 ELSE (p.precioUnidad <= _precio_max) END)
    AND
      (CASE WHEN _largo_min  IS NULL THEN 1 ELSE (f.largo >= _largo_min) END)
    AND
      (CASE WHEN _largo_max  IS NULL THEN 1 ELSE (f.largo <= _largo_max) END)
    AND
      (CASE WHEN _solo_activos IS NULL THEN 1 ELSE (p.activo = _solo_activos) END);
END$$

DELIMITER ;

-- -----------------------------------------------------
-- procedure filtrar_pantalones
-- -----------------------------------------------------

USE `mydb`;
DROP procedure IF EXISTS `mydb`.`filtrar_pantalones`;

DELIMITER $$
USE `mydb`$$
CREATE DEFINER=`admin`@`%` PROCEDURE `filtrar_pantalones`(
    IN _nombre_like         VARCHAR(100),
    IN _color_like          VARCHAR(30),
    IN _material_like       VARCHAR(50),
    IN _tipo_pantalon_like  VARCHAR(30),
    IN _elasticidad         TINYINT,         -- NULL: no filtra; 1: solo elásticos; 0: solo no elásticos
    IN _precio_min          DECIMAL(10,2),
    IN _precio_max          DECIMAL(10,2),
    IN _largo_min           DECIMAL(10,2),
    IN _largo_max           DECIMAL(10,2),
    IN _cintura_min         DECIMAL(10,2),
    IN _cintura_max         DECIMAL(10,2),
    IN _solo_activos        TINYINT          -- NULL no filtra; 1 activos; 0 inactivos
)
BEGIN
  SELECT
      p.*,
      pa.tipo_pantalon,
      pa.largo_pierna,
      pa.cintura,
      pa.elasticidad
  FROM Prenda p
  JOIN Pantalon pa ON pa.idPrenda = p.idPrenda
  WHERE
      (CASE 
         WHEN _nombre_like IS NULL OR TRIM(_nombre_like) = '' THEN 1
         ELSE (LOWER(p.nombre) LIKE CONCAT('%', LOWER(TRIM(_nombre_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _color_like IS NULL OR TRIM(_color_like) = '' THEN 1
         ELSE (LOWER(p.color) LIKE CONCAT('%', LOWER(TRIM(_color_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _material_like IS NULL OR TRIM(_material_like) = '' THEN 1
         ELSE (LOWER(p.material) LIKE CONCAT('%', LOWER(TRIM(_material_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _tipo_pantalon_like IS NULL OR TRIM(_tipo_pantalon_like) = '' THEN 1
         ELSE (LOWER(pa.tipo_pantalon) LIKE CONCAT('%', LOWER(TRIM(_tipo_pantalon_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _elasticidad IS NULL THEN 1
         ELSE (pa.elasticidad = _elasticidad)
       END)
    AND
      (CASE WHEN _precio_min  IS NULL THEN 1 ELSE (p.precioUnidad  >= _precio_min)  END)
    AND
      (CASE WHEN _precio_max  IS NULL THEN 1 ELSE (p.precioUnidad  <= _precio_max)  END)
    AND
      (CASE WHEN _largo_min   IS NULL THEN 1 ELSE (pa.largo_pierna >= _largo_min)   END)
    AND
      (CASE WHEN _largo_max   IS NULL THEN 1 ELSE (pa.largo_pierna <= _largo_max)   END)
    AND
      (CASE WHEN _cintura_min IS NULL THEN 1 ELSE (pa.cintura      >= _cintura_min) END)
    AND
      (CASE WHEN _cintura_max IS NULL THEN 1 ELSE (pa.cintura      <= _cintura_max) END)
    AND
      (CASE WHEN _solo_activos IS NULL THEN 1 ELSE (p.activo = _solo_activos) END);
END$$

DELIMITER ;

-- -----------------------------------------------------
-- procedure filtrar_polos
-- -----------------------------------------------------

USE `mydb`;
DROP procedure IF EXISTS `mydb`.`filtrar_polos`;

DELIMITER $$
USE `mydb`$$
CREATE DEFINER=`admin`@`%` PROCEDURE `filtrar_polos`(
    IN _nombre_like       VARCHAR(100),
    IN _color_like        VARCHAR(30),
    IN _material_like     VARCHAR(50),
    IN _tipo_cuello_like  VARCHAR(30),
    IN _tipo_manga_like   VARCHAR(30),
    IN _precio_min        DECIMAL(10,2),
    IN _precio_max        DECIMAL(10,2),
    IN _solo_activos      TINYINT
)
BEGIN
  /* Nota:
     - LIKE sobre strings (incluyendo ENUMs).
     - Si un parámetro llega NULL o vacío, no filtra por ese campo.
  */
  SELECT 
      p.*,
      po.tipo_manga,
      po.estampado,
      po.tipo_cuello
  FROM Prenda p
  JOIN Polo   po ON po.idPrenda = p.idPrenda
  WHERE
      (CASE 
         WHEN _nombre_like IS NULL OR TRIM(_nombre_like) = '' THEN 1
         ELSE (LOWER(p.nombre) LIKE CONCAT('%', LOWER(TRIM(_nombre_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _color_like IS NULL OR TRIM(_color_like) = '' THEN 1
         ELSE (LOWER(p.color) LIKE CONCAT('%', LOWER(TRIM(_color_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _material_like IS NULL OR TRIM(_material_like) = '' THEN 1
         ELSE (LOWER(p.material) LIKE CONCAT('%', LOWER(TRIM(_material_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _tipo_cuello_like IS NULL OR TRIM(_tipo_cuello_like) = '' THEN 1
         ELSE (LOWER(po.tipo_cuello) LIKE CONCAT('%', LOWER(TRIM(_tipo_cuello_like)), '%'))
       END)
    AND
      (CASE 
         WHEN _tipo_manga_like IS NULL OR TRIM(_tipo_manga_like) = '' THEN 1
         ELSE (LOWER(po.tipo_manga) LIKE CONCAT('%', LOWER(TRIM(_tipo_manga_like)), '%'))
       END)
    AND
      (CASE WHEN _precio_min IS NULL THEN 1 ELSE (p.precioUnidad >= _precio_min) END)
    AND
      (CASE WHEN _precio_max IS NULL THEN 1 ELSE (p.precioUnidad <= _precio_max) END)
    AND
      (CASE WHEN _solo_activos IS NULL THEN 1 ELSE (p.activo = _solo_activos) END);
END$$

DELIMITER ;

