DELIMITER $$
USE `mydb`$$
CREATE DEFINER=`admin`@`%` PROCEDURE `obtener_stock_prenda_talla`(
    IN _id_prenda INT,
    IN _talla     ENUM('XS','S','M','L','XL','XXL')
)
BEGIN
  SELECT COALESCE(SUM(stock), 0) AS stock_total
    FROM PrendaLote
   WHERE Prenda_idPrenda = _id_prenda
     AND talla           = _talla
     AND activo          = 1;
